import { reqGetCode, reqRegisterCom, reqUserLogin, reqUserInfo } from '@/api'

const state = {
  code: [],
  token: '',
  userInfo: {},
}
const mutations = {
  REQGETCODE(state, code) {
    state.code = code
  },
  USERLOGIN(state, token) {
    state.token = token
  },
  USERINFO(state, userInfo) {
    state.userInfo = userInfo
  },
}

const getters = {}
const actions = {
  async reqCode({ commit }, phone) {
    let result = await reqGetCode(phone)
    if (result.code === 200) {
      commit('REQGETCODE', result.data)
    } else {
      return Promise.reject(new Error('fail'))
    }
  },
  async reqGetRegisterCom({ commit }, data) {
    let result = await reqRegisterCom(data)
    if (result.code === 200) {
      return 'ok'
    } else {
      return Promise.reject(new Error('fail'))
    }
  },

  async reqGetUserLogin({ commit }, data) {
    let result = await reqUserLogin(data)
    if (result.code === 200) {
      commit('USERLOGIN', result.data.token)
      return 'ok'
    } else {
      return Promise.reject(new Error('fail'))
    }
  },

  async reqGetUserInfo({ commit }) {
    let result = await reqUserInfo()
    if (result.code === 200) {
      commit('USERINFO', result.data)
      return 'ok'
    } else {
      return Promise.reject(new Error('fail'))
    }
  },
}

export default {
  state,
  mutations,
  getters,
  actions,
}
