import { reqDetail } from '@/api'

const state = {
  detail: {},
}

const mutations = {
  REQDETAIL(state, detail) {
    state.detail = detail
  },
}

const getters = {}
const actions = {
  async reqGetDetail({ commit }, skuid) {
    console.log(skuid)
    let result = await reqDetail(skuid)
    if (result.code === 200) commit('REQDETAIL', result.data)
  },
}

export default {
  state,
  mutations,
  getters,
  actions,
}
