import { reqShopList, reqaddToCart } from '@/api'

const state = {
  shopList: [],
}

const mutations = {
  REQSHOPLIST(state, shopList) {
    console.log(shopList)
    state.shopList = shopList[0].cartInfoList
  },
}

const getters = {}
const actions = {
  async reqGetShopList({ commit }) {
    let result = await reqShopList()
    if (result.code === 200) {
      commit('REQSHOPLIST', result.data)
    } else {
      return Promise.reject(new Error('fail'))
    }
  },
  async reqGetaddToCart({ skuId, skuNum }) {
    let result = await reqaddToCart(skuId, skuNum)
    if (result.code === 200) {
      console.log(result)
    } else {
      return Promise.reject(new Error('fail'))
    }
  },
}

export default {
  state,
  mutations,
  getters,
  actions,
}
