import VueRouter from 'vue-router'
import Vue from 'vue'

Vue.use(VueRouter)

import Home from '@/pages/Home'
import Search from '@/pages/Search'
import Login from '@/pages/Login'
import Register from '@/pages/Register'
import Detail from '@/pages/Detail'
import ShopCart from '@/pages/ShopCart'
import AddCartSuccess from '@/pages/AddCartSuccess'
import store from '@/store'

let router = new VueRouter({
  routes: [
    {
      name: 'cartlist',
      path: '/cartlist',
      component: ShopCart,
      meta: { show: true },
    },
    {
      name: 'addcartsuccess',
      path: '/addcartsuccess',
      component: AddCartSuccess,
      meta: { show: true },
    },
    {
      name: 'home',
      path: '/home',
      component: Home,
      meta: { show: true },
    },
    {
      name: 'detail',
      path: '/detail/:skuid',
      component: Detail,
      meta: { show: true },
    },
    {
      name: 'search',
      path: '/search/:keyword?',
      component: Search,
      meta: { show: true },
    },
    {
      name: 'login',
      path: '/login',
      component: Login,
      meta: { show: false },
    },
    {
      name: 'register',
      path: '/register',
      component: Register,
      meta: { show: false },
    },
    {
      path: '*',
      redirect: '/home',
    },
  ],
  scrollBehavior(to, from, savedPosition) {
    return { y: 0 }
  },
})

