import { reqDetail } from '@/api'

const state = {
  details: {},
}

const mutations = {
  REQDETAIL(state, detail) {
    state.detail = detail
  },
}

const getters = {}
const actions = {
  async reqDetail({ commit }) {
    let result = await reqDetail()
    if (result.code === 200) commit('REQDETAIL', result.data)
  },
}

export default {
  state,
  mutations,
  getters,
  actions,
}
